// App.js

import React from 'react';
import './App.css';

const getRandomCard = () => {
  return Math.floor(Math.random() * 13) + 1;
};

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      currentCard: 0,
      nextCard: 0,
      result: '',
      gameOver: true
    };
  }

  startGame = () => {
    this.setState({
      currentCard: getRandomCard(),
      nextCard: getRandomCard(),
      result: '',
      gameOver: false
    });
  };

  handleHigher = () => {
    const newNextCard = getRandomCard();
    if (newNextCard > this.state.nextCard) {
      this.setState({
        result: 'You guessed right! It was higher.',
        currentCard: this.state.nextCard,
        nextCard: newNextCard
      });
    } else {
      this.setState({
        result: 'You guessed wrong! It was not higher.',
        gameOver: true
      });
    }
  };

  handleLower = () => {
    const newNextCard = getRandomCard();
    if (newNextCard < this.state.nextCard) {
      this.setState({
        result: 'You guessed right! It was lower.',
        currentCard: this.state.nextCard,
        nextCard: newNextCard
      });
    } else {
      this.setState({
        result: 'You guessed wrong! It was not lower.',
        gameOver: true
      });
    }
  };

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <h1>High-Low Card Game</h1>
          <div>
            <p>Current Card: {this.state.currentCard}</p>
            <p>Next Card: {this.state.nextCard}</p>
            <p>Result: {this.state.result}</p>
          </div>
          {this.state.gameOver ? (
            <button onClick={this.startGame}>Start Game</button>
          ) : (
            <>
              <button onClick={this.handleHigher}>Higher</button>
              <button onClick={this.handleLower}>Lower</button>
            </>
          )}
        </header>
      </div>
    );
  }
}

export default App;
