// App.js
import React, { useState } from 'react';
import './App.css'; // Import CSS for styling

function App() {
  const [numItems, setNumItems] = useState(0);
  const [items, setItems] = useState([]);
  const [capacity, setCapacity] = useState(0);
  const [knapsackType, setKnapsackType] = useState(0);
  const [maxValue, setMaxValue] = useState(0);
  const [selectedItems, setSelectedItems] = useState([]);

  const handleCalculate = () => {
    const requestData = {
      num_items: numItems,
      items: items,
      capacity: capacity,
      knapsack_type: knapsackType
    };

    fetch('http://localhost:5000/calculate', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(requestData)
    })
    .then(response => response.json())
    .then(data => {
      setMaxValue(data.max_value);
      setSelectedItems(data.selected_items);
    })
    .catch(error => {
      console.error('Error:', error);
    });
  };

  return (
    <div>
      <h1>Knapsack Solver</h1>
      <label>Number of Items: </label>
      <input type="number" value={numItems} onChange={(e) => setNumItems(parseInt(e.target.value))} />
      <button onClick={handleAddItem}>Add Items</button>
      <br />
      {items.map((item, index) => (
        <div key={index}>
          <label>Item {index + 1} Weight: </label>
          <input type="number" value={item.weight} onChange={(e) => handleItemChange(index, parseFloat(e.target.value), 'weight')} />
          <label> Profit: </label>
          <input type="number" value={item.profit} onChange={(e) => handleItemChange(index, parseFloat(e.target.value), 'profit')} />
        </div>
      ))}
      <br />
      <label>Knapsack Capacity: </label>
      <input type="number" value={capacity} onChange={(e) => setCapacity(parseFloat(e.target.value))} />
      <br />
      <button onClick={solveKnapsack}>Solve Knapsack</button>
      <button onClick={handleClear}>Clear Result</button>
      <br />
      <h2>Selected Items:</h2>
      {selectedItems.map((item, index) => (
        <div key={index}>
          <p>Item Weight: {item.weight}</p>
          <p>Item Profit: {item.profit}</p>
        </div>
      ))}
      <h2>Total Value: {totalValue}</h2>
    </div>
  );
}

function ItemsInput({ numItems, setItems }) {
  const [weights, setWeights] = useState([]);
  const [profits, setProfits] = useState([]);

  const handleInputChange = (index, e) => {
    const { name, value } = e.target;
    if (name === 'weight') {
      const newWeights = [...weights];
      newWeights[index] = value;
      setWeights(newWeights);
    } else if (name === 'profit') {
      const newProfits = [...profits];
      newProfits[index] = value;
      setProfits(newProfits);
    }
  };

  const renderInputs = () => {
    const inputs = [];
    for (let i = 0; i < numItems; i++) {
      inputs.push(
        <div key={i}>
          <label>Item {i + 1} Weight: </label>
          <input type="number" name="weight" value={weights[i] || ''} onChange={e => handleInputChange(i, e)} /><br />
          <label>Item {i + 1} Profit: </label>
          <input type="number" name="profit" value={profits[i] || ''} onChange={e => handleInputChange(i, e)} /><br />
        </div>
      );
    }
    return inputs;
  };

  React.useEffect(() => {
    const newItems = [];
    for (let i = 0; i < numItems; i++) {
      newItems.push([parseFloat(weights[i]) || 0, parseFloat(profits[i]) || 0]);
    }
    setItems(newItems);
  }, [weights, profits, numItems, setItems]);

  return (
    <div className="input-container">
      {renderInputs()}
    </div>
  );
}

export default App;
