import React, { useState, useEffect } from 'react';
import './App.css';

const operators = ['+', '-', '*', '/'];

const generateQuestion = () => {
  const num1 = Math.floor(Math.random() * 10) + 1;
  const num2 = Math.floor(Math.random() * 10) + 1;
  const operator = operators[Math.floor(Math.random() * operators.length)];
  let answer;
  switch (operator) {
    case '+':
      answer = num1 + num2;
      break;
    case '-':
      answer = num1 - num2;
      break;
    case '*':
      answer = num1 * num2;
      break;
    case '/':
      answer = parseFloat((num1 / num2).toFixed(2));
      break;
    default:
      break;
  }

  const options = [answer];
  while (options.length < 4) {
    const option = Math.floor(Math.random() * 20) - 10;
    if (!options.includes(option)) {
      options.push(option);
    }
  }
  options.sort(() => Math.random() - 0.5);

  return { num1, num2, operator, answer, options };
};

const Question = ({ question, timeLeft, onAnswer, questionNumber }) => {
  return (
    <div className="question">
      <div className="question-number">Question {questionNumber}/25</div>
      <div className="question-text">
        <div className="slot">{question.num1}</div>
        <div className="slot">{question.operator}</div>
        <div className="slot">{question.num2}</div>
      </div>
      <div className="options">
        {question.options.map((option, index) => (
          <button key={index} onClick={() => onAnswer(option)}>
            {option}
          </button>
        ))}
      </div>
      <div className="timer">Time left: {timeLeft}s</div>
    </div>
  );
};

const Game = ({ playerName, onGameEnd }) => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [timeLeft, setTimeLeft] = useState(30);

  useEffect(() => {
    const generatedQuestions = Array.from({ length: 25 }, generateQuestion);
    setQuestions(generatedQuestions);
  }, []);

  useEffect(() => {
    if (timeLeft > 0) {
      const timer = setInterval(() => {
        setTimeLeft(timeLeft - 1);
      }, 1000);
      return () => clearInterval(timer);
    } else {
      nextQuestion(0);
    }
  }, [timeLeft]);

  const nextQuestion = (points) => {
    setScore(score + points);
    if (currentQuestion < 24) {
      setCurrentQuestion(currentQuestion + 1);
      setTimeLeft(30 - currentQuestion * 1); // Decrease time gradually
    } else {
      onGameEnd(score);
    }
  };

  const handleAnswer = (option) => {
    if (questions[currentQuestion].answer === option) {
      nextQuestion(3);
    } else {
      nextQuestion(-1);
    }
  };

  return (
    <div className="game">
      {questions.length > 0 && (
        <Question
          question={questions[currentQuestion]}
          timeLeft={timeLeft}
          onAnswer={handleAnswer}
          questionNumber={currentQuestion + 1}
        />
      )}
      <div className="score">Score: {score}</div>
    </div>
  );
};

const WinScreen = ({ playerName, score, onReplay }) => {
  return (
    <div className="win-screen">
      <h1>Congratulations, {playerName}!</h1>
      <p>Your final score is: {score}</p>
      <button onClick={onReplay}>Play Again</button>
    </div>
  );
};

function App() {
  const [playerName, setPlayerName] = useState('');
  const [gameStarted, setGameStarted] = useState(false);
  const [gameEnded, setGameEnded] = useState(false);
  const [finalScore, setFinalScore] = useState(0);

  const startGame = () => {
    if (playerName.trim() !== '') {
      setGameStarted(true);
      setGameEnded(false);
      setFinalScore(0);
    }
  };

  const endGame = (score) => {
    setGameEnded(true);
    setGameStarted(false);
    setFinalScore(score);
  };

  const replayGame = () => {
    setGameStarted(true);
    setGameEnded(false);
    setFinalScore(0);
  };

  return (
    <div className="App">
      {!gameStarted && !gameEnded ? (
        <div className="start-screen">
          <h1>Welcome to the Math Puzzle Game</h1>
          <input
            type="text"
            placeholder="Enter your name"
            value={playerName}
            onChange={(e) => setPlayerName(e.target.value)}
          />
          <button onClick={startGame}>Start</button>
        </div>
      ) : gameStarted ? (
        <Game playerName={playerName} onGameEnd={endGame} />
      ) : (
        <WinScreen playerName={playerName} score={finalScore} onReplay={replayGame} />
      )}
    </div>
  );
}

export default App;
