import React, { useState, useEffect } from 'react';
import './App.css';

const operators = ['+', '-', '*', '/', '%'];

const generateRandomNumber = () => Math.floor(Math.random() * 90) + 10; // Two-digit numbers from 10 to 99
const generateRandomOperator = () => operators[Math.floor(Math.random() * operators.length)];

const generateQuestion = () => {
  const num1 = generateRandomNumber();
  const num2 = generateRandomNumber();
  const operator = generateRandomOperator();
  let answer;
  switch (operator) {
    case '+':
      answer = num1 + num2;
      break;
    case '-':
      answer = num1 - num2;
      break;
    case '*':
      answer = num1 * num2;
      break;
    case '/':
      answer = parseFloat((num1 / num2).toFixed(2)); // Round to 2 decimal places
      break;
    case '%':
      answer = num1 % num2;
      break;
    default:
      break;
  }

  const options = [answer];
  while (options.length < 4) {
    const option = (Math.random() < 0.5 ? -1 : 1) * (Math.floor(Math.random() * 5) + 1) + answer;
    if (!options.includes(option)) {
      options.push(parseFloat(option.toFixed(2))); // Ensure options are rounded to 2 decimal places
    }
  }
  options.sort(() => Math.random() - 0.5);

  return { num1, num2, operator, answer, options };
};

const Question = ({ question, timeLeft, onAnswer, questionNumber }) => {
  const [displayNum1, setDisplayNum1] = useState(generateRandomNumber());
  const [displayNum2, setDisplayNum2] = useState(generateRandomNumber());
  const [displayOperator, setDisplayOperator] = useState(generateRandomOperator());
  const [selectedOption, setSelectedOption] = useState(null);

  useEffect(() => {
    let interval1, interval2, intervalOperator, timeout;
    interval1 = setInterval(() => setDisplayNum1(generateRandomNumber()), 50);
    interval2 = setInterval(() => setDisplayNum2(generateRandomNumber()), 50);
    intervalOperator = setInterval(() => setDisplayOperator(generateRandomOperator()), 50);

    timeout = setTimeout(() => {
      clearInterval(interval1);
      clearInterval(interval2);
      clearInterval(intervalOperator);
      setDisplayNum1(question.num1);
      setDisplayNum2(question.num2);
      setDisplayOperator(question.operator);
    }, 1500);

    return () => {
      clearInterval(interval1);
      clearInterval(interval2);
      clearInterval(intervalOperator);
      clearTimeout(timeout);
    };
  }, [question]);

  const handleOptionClick = (option) => {
    setSelectedOption(option);
    setTimeout(() => onAnswer(option), 1000);
  };

  return (
    <div className="question">
      <div className="question-number">Question {questionNumber}/25</div>
      <div className="slot-machine">
        <div className="slot">{displayNum1}</div>
        <div className="slot">{displayOperator}</div>
        <div className="slot">{displayNum2}</div>
      </div>
      <div className="options">
        {question.options.map((option, index) => (
          <button
            key={index}
            onClick={() => handleOptionClick(option)}
            className={selectedOption === option ? (option === question.answer ? 'correct' : 'incorrect') : ''}
          >
            {option}
          </button>
        ))}
      </div>
      <div className="timer">Time left: {timeLeft}s</div>
    </div>
  );
};

const Game = ({ playerName, onGameOver }) => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [timeLeft, setTimeLeft] = useState(30);

  useEffect(() => {
    const generatedQuestions = Array.from({ length: 25 }, generateQuestion);
    setQuestions(generatedQuestions);
  }, []);

  useEffect(() => {
    if (timeLeft > 0) {
      const timer = setInterval(() => {
        setTimeLeft(timeLeft - 1);
      }, 1000);
      return () => clearInterval(timer);
    } else {
      nextQuestion(0);
    }
  }, [timeLeft, currentQuestion]);

  const nextQuestion = (points) => {
    setScore(score + points);
    if (currentQuestion < 24) {
      setCurrentQuestion(currentQuestion + 1);
      setTimeLeft(30 - currentQuestion * 1);
    } else {
      onGameOver(score + points);
    }
  };

  const handleAnswer = (option) => {
    if (questions[currentQuestion].answer === option) {
      nextQuestion(3);
    } else {
      nextQuestion(-1);
    }
  };

  return (
    <div className="game">
      {questions.length > 0 && (
        <Question
          question={questions[currentQuestion]}
          timeLeft={timeLeft}
          onAnswer={handleAnswer}
          questionNumber={currentQuestion + 1}
        />
      )}
      <div className="score">Score: {score}</div>
    </div>
  );
};

const WinScreen = ({ score, playerName, onRestart }) => {
  return (
    <div className="win-screen">
      <h1>Well Done, {playerName}!</h1>
      <p>You scored {score}</p>
      <button onClick={onRestart}>Play Again</button>
    </div>
  );
};

function App() {
  const [playerName, setPlayerName] = useState('');
  const [gameStarted, setGameStarted] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [finalScore, setFinalScore] = useState(0);

  const startGame = () => {
    if (playerName.trim() !== '') {
      setGameStarted(true);
      setGameOver(false);
      setFinalScore(0);
    }
  };

  const handleGameOver = (score) => {
    setGameOver(true);
    setGameStarted(false);
    setFinalScore(score);
  };

  const restartGame = () => {
    setPlayerName('');
    setGameStarted(false);
    setGameOver(false);
    setFinalScore(0);
  };

  return (
    <div className="App">
      {!gameStarted && !gameOver ? (
        <div className="start-screen">
          <h1>Number Slot Machine</h1>
          <input
            type="text"
            placeholder="Enter your name"
            value={playerName}
            onChange={(e) => setPlayerName(e.target.value)}
          />
          <button onClick={startGame}>Start</button>
        </div>
      ) : gameStarted ? (
        <Game playerName={playerName} onGameOver={handleGameOver} />
      ) : (
        <WinScreen score={finalScore} playerName={playerName} onRestart={restartGame} />
      )}
    </div>
  );
}

export default App;
