import React, { useState, useEffect } from 'react';
import './App.css';

const operators = ['+', '-', '*', '/'];

const generateQuestion = () => {
  const num1 = Math.floor(Math.random() * 10) + 1;
  const num2 = Math.floor(Math.random() * 10) + 1;
  const operator = operators[Math.floor(Math.random() * operators.length)];
  let answer;
  switch (operator) {
    case '+':
      answer = num1 + num2;
      break;
    case '-':
      answer = num1 - num2;
      break;
    case '*':
      answer = num1 * num2;
      break;
    case '/':
      answer = parseFloat((num1 / num2).toFixed(2));
      break;
    default:
      break;
  }

  const options = [answer];
  while (options.length < 4) {
    const option = Math.floor(Math.random() * 20) - 10;
    if (!options.includes(option)) {
      options.push(option);
    }
  }
  options.sort(() => Math.random() - 0.5);

  return { num1, num2, operator, answer, options };
};

const Question = ({ question, timeLeft, onAnswer }) => {
  return (
    <div className="question">
      <div className="question-text">
        {question.num1} {question.operator} {question.num2}
      </div>
      <div className="options">
        {question.options.map((option, index) => (
          <button key={index} onClick={() => onAnswer(option)}>
            {option}
          </button>
        ))}
      </div>
      <div className="timer">Time left: {timeLeft}s</div>
    </div>
  );
};

const Game = ({ playerName }) => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [score, setScore] = useState(0);
  const [timeLeft, setTimeLeft] = useState(30);

  useEffect(() => {
    const generatedQuestions = Array.from({ length: 25 }, generateQuestion);
    setQuestions(generatedQuestions);
  }, []);

  useEffect(() => {
    if (timeLeft > 0) {
      const timer = setInterval(() => {
        setTimeLeft(timeLeft - 1);
      }, 1000);
      return () => clearInterval(timer);
    } else {
      nextQuestion(0);
    }
  }, [timeLeft]);

  const nextQuestion = (points) => {
    setScore(score + points);
    if (currentQuestion < 24) {
      setCurrentQuestion(currentQuestion + 1);
      setTimeLeft(30 - currentQuestion * 1); // Decrease time gradually
    } else {
      alert(`Game Over! ${playerName}, your score is: ${score}`);
    }
  };

  const handleAnswer = (option) => {
    if (questions[currentQuestion].answer === option) {
      nextQuestion(3);
    } else {
      nextQuestion(-1);
    }
  };

  return (
    <div className="game">
      {questions.length > 0 && (
        <Question
          question={questions[currentQuestion]}
          timeLeft={timeLeft}
          onAnswer={handleAnswer}
        />
      )}
      <div className="score">Score: {score}</div>
    </div>
  );
};

function App() {
  const [playerName, setPlayerName] = useState('');
  const [gameStarted, setGameStarted] = useState(false);

  const startGame = () => {
    if (playerName.trim() !== '') {
      setGameStarted(true);
    }
  };

  return (
    <div className="App">
      {!gameStarted ? (
        <div className="start-screen">
          <h1>Welcome to the Math Puzzle Game</h1>
          <input
            type="text"
            placeholder="Enter your name"
            value={playerName}
            onChange={(e) => setPlayerName(e.target.value)}
          />
          <button onClick={startGame}>Start</button>
        </div>
      ) : (
        <Game playerName={playerName} />
      )}
    </div>
  );
}

export default App;
