import React, { useState } from 'react';
import './App.css';
import avatar from './amulya_avatar.png';

function App() {
  const [currentSection, setCurrentSection] = useState('home');
  const handleNavClick = (section) => {
    setCurrentSection(section);
  };
  return (
    <div className="App">
      <Navbar handleNavClick={handleNavClick} />
      <div className="content">
        {currentSection === 'home' && <Home />}
        {currentSection === 'projects' && <Projects />}
        {currentSection === 'about' && <About />}
        {currentSection === 'contact' && <Contact />}
      </div>
    </div>
  );
}

function Navbar({ handleNavClick }) {
  return (
    <nav className="navbar">
      <ul>
        <li><button onClick={() => handleNavClick('home')}>Overview</button></li>
        <li><button onClick={() => handleNavClick('projects')}>Projects</button></li>
        <li><button onClick={() => handleNavClick('about')}>About</button></li>
        <li><button onClick={() => handleNavClick('contact')}>Contact Info</button></li>
      </ul>
    </nav>
  );
}

function Home() {
  return (
    <section id="home" className="section">
      <div className="overview">
        <div className="avatar">
          <img src={avatar} alt="Amulya Parashar Avatar" />
        </div>
        <div className="overview-content">
          <h2><strong>Amulya Parashar</strong></h2>
          <p>
            <ul>
              <li>Student in <strong>MIT Manipal</strong>.</li>
              <li>Proficient Coder in<strong> 
                <ul>
                  <li>C/C++</li>
                  <li>Java/JavaFX</li>
                  <li>MySQL/SQLPlus</li>
                  <li>Python</li>
                  <li>HTML CSS and Javascript</li>
                </ul>
              </strong></li>
            </ul>
            My skill sets extend to <strong>Data Structures and Algorithms (DSA), Object Oriented Programming (OOPs), Power BI, and Data Analytics</strong>.
          </p>
        </div>
      </div>
    </section>
  );
}


export default App;
