// App.js

import React, { useState, useEffect } from 'react';
import './App.css';

const GRID_SIZE = 20;

const FrontPage = ({ onStart }) => (
  <div className="front-page">
    <h1>Snake Xenia</h1>
    <button onClick={onStart}>Start</button>
  </div>
);

const GameOverPage = ({ onRestart, score }) => (
  <div className="game-over-page">
    <h1>Game Over!</h1>
    <p>Your Score: {score}</p>
    <button onClick={onRestart}>Restart</button>
  </div>
);

const App = () => {
  const [page, setPage] = useState('front');
  const [grid, setGrid] = useState([]);
  const [snake, setSnake] = useState([{ x: 10, y: 10 }]);
  const [food, setFood] = useState({ x: 15, y: 15 });
  const [direction, setDirection] = useState('RIGHT');
  const [score, setScore] = useState(0);
  const [isPaused, setIsPaused] = useState(false);

  useEffect(() => {
    const updateGrid = () => {
      const newGrid = [];
      for (let i = 0; i < GRID_SIZE; i++) {
        const row = [];
        for (let j = 0; j < GRID_SIZE; j++) {
          row.push(0);
        }
        newGrid.push(row);
      }
      snake.forEach(segment => {
        newGrid[segment.y][segment.x] = 1;
      });
      newGrid[food.y][food.x] = 2;
      setGrid(newGrid);
    };
    updateGrid();
  }, [snake, food]);

  useEffect(() => {
    const interval = setInterval(() => {
      if (!isPaused) moveSnake();
    }, 200);
    return () => clearInterval(interval);
  }, [snake, isPaused]);

  useEffect(() => {
    const handleKeyPress = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          if (direction !== 'DOWN') setDirection('UP');
          break;
        case 'ArrowDown':
          if (direction !== 'UP') setDirection('DOWN');
          break;
        case 'ArrowLeft':
          if (direction !== 'RIGHT') setDirection('LEFT');
          break;
        case 'ArrowRight':
          if (direction !== 'LEFT') setDirection('RIGHT');
          break;
        case 'p':
        case 'P':
          togglePause();
          break;
        default:
          break;
      }
    };

    document.addEventListener('keydown', handleKeyPress);

    return () => {
      document.removeEventListener('keydown', handleKeyPress);
    };
  }, [direction, isPaused]);

  const moveSnake = () => {
    const newSnake = [...snake];
    const head = { ...newSnake[0] };

    switch (direction) {
      case 'UP':
        head.y = (head.y - 1 + GRID_SIZE) % GRID_SIZE;
        break;
      case 'DOWN':
        head.y = (head.y + 1) % GRID_SIZE;
        break;
      case 'LEFT':
        head.x = (head.x - 1 + GRID_SIZE) % GRID_SIZE;
        break;
      case 'RIGHT':
        head.x = (head.x + 1) % GRID_SIZE;
        break;
      default:
        break;
    }

    // Check for collision with walls
    if (head.x < 0 || head.x >= GRID_SIZE || head.y < 0 || head.y >= GRID_SIZE) {
      wrapAround(head);
    }

    const collided = newSnake.slice(1).some(segment => segment.x === head.x && segment.y === head.y);
    if (collided) {
      // Game over
      setPage('gameOver');
      return;
    }

    newSnake.unshift(head);
    if (head.x === food.x && head.y === food.y) {
      generateFood();
      setScore(score + 1); // Increment score
    } else {
      newSnake.pop();
    }

    setSnake(newSnake);
  };

  const wrapAround = (head) => {
    // Wrap around to opposite wall
    if (head.x < 0) head.x = GRID_SIZE - 1;
    if (head.x >= GRID_SIZE) head.x = 0;
    if (head.y < 0) head.y = GRID_SIZE - 1;
    if (head.y >= GRID_SIZE) head.y = 0;
  };

  const generateFood = () => {
    const newFood = {
      x: Math.floor(Math.random() * GRID_SIZE),
      y: Math.floor(Math.random() * GRID_SIZE)
    };
    setFood(newFood);
  };

  const handleStart = () => {
    setPage('game');
    setScore(0); // Reset score
  };

  const handleRestart = () => {
    setPage('game');
    setSnake([{ x: 10, y: 10 }]);
    setDirection('RIGHT');
    setScore(0); // Reset score
    setIsPaused(false); // Unpause the game
  };

  const togglePause = () => {
    setIsPaused(!isPaused);
  };

  return (
    <div className="App">
      <div className="game">
        {page === 'front' && <FrontPage onStart={handleStart} />}
        {page === 'gameOver' && <GameOverPage onRestart={handleRestart} score={score} />}
        {page === 'game' && (
          <>
            <div className="score">Score: {score}</div>
            <button className="pause-button" onClick={togglePause}>
              {isPaused ? 'Resume' : 'Pause'}
            </button>
            {grid.map((row, rowIndex) => (
              <div key={rowIndex} className="row">
                {row.map((cell, colIndex) => (
                  <div key={colIndex} className={`cell ${cell === 1 ? 'snake' : cell === 2 ? 'food' : ''}`}></div>
                ))}
              </div>
            ))}
          </>
        )}
      </div>
    </div>
  );
};

export default App;
